const initialState = {
  books: [],
  loading: true,
  error: null,
  cartItems: [],
  orderTotal: 180
}
const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'FETCH_BOOKS_SUCCESS':
      return {
        ...state,
        books: action.payload,
        loading: false,
        error: null,
      };
    case 'FETCH_BOOKS_REQUEST':
      return {
        ...state,
        books: [],
        loading: true,
        error: null,
      };
    case 'FETCH_BOOKS_FAILURE':
      return {
        ...state,
        books: [],
        loading: false,
        error: action.payload
    };
    case 'ADD_BOOK_TO_CART':
      //  Сохраняем id книги
      const id = action.payload;
      // Сохраняем тело книги из массива books
      const book = state.books.find((item) => item.id === id);
      // Получаем индекс
      const indexInCart = state.books.findIndex((item) => item.id === id);
      // Получем элемент из корзины по индксу, если элемента нет, то item будет undefined
      const item = state.cartItems[indexInCart];
      let newItem;

      // Если элемента нет, то создаем новый, а просто добавляем его в конец массива cartItems
      if (!item) {
        newItem = {
          id: book.id,
          title: book.title,
          count: 1,
          total: book.price,
        }
        return {
          ...state,
          cartItems: [
            ...state.cartItems,
            newItem,
          ]
        }
      //  Если элемент есть, то мы его модифицируем, и ставляем его в середину массива
      } else {
        newItem = {
          ...item,
          count: item.count + 1,
          total: item.total + book.price,
        }

        return {
          ...state,
          cartItems: [
            ...state.cartItems.slice(0, )
          ]
        }
      }

    default:
      return state;
  }
}

export default reducer;
