const initialState = {
  books: [],
  loading: true,
  error: null,
  cartItems: [],
  orderTotal: 180
}
const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'FETCH_BOOKS_SUCCESS':
      return {
        ...state,
        books: action.payload,
        loading: false,
        error: null,
      };
    case 'FETCH_BOOKS_REQUEST':
      return {
        ...state,
        books: [],
        loading: true,
        error: null,
      };
    case 'FETCH_BOOKS_FAILURE':
      return {
        ...state,
        books: [],
        loading: false,
        error: action.payload
    };
    case 'ADD_BOOK_TO_CART':
      const item = state.books.find((item) => item.id === action.payload);
      const idx = state.cartItems.findIndex((item) => item.id === action.payload);
      if (idx === -1) {
        const newItem = {
          id: item.id,
          name: item.title,
          count: 1,
          total: item.price,
        }
        return {
          ...state,
          cartItems: [
            ...state.cartItems,
            newItem,
          ]
        }
      } else {
        const newObj = {
          ...state,
          cartItems: [
            state.cartItems,
          ]
        }
        console.log(newObj.cartItems[0]);
        // newObj.cartItems[0].count = newObj.cartItems[0].count + 1;
        debugger
        return newObj;
      }

    default:
      return state;
  }
}

export default reducer;
